import React, { useState, useEffect } from "react";
import axios from "axios";
import PatientList from "./components/PatientList";
import AddPatientForm from "./components/AddPatientForm";
import "./styles.css";

function App() {
  const [patients, setPatients] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [notification, setNotification] = useState("");

  useEffect(() => {
    fetchPatients();
  }, []);

  const fetchPatients = async () => {
    setIsLoading(true);
    try {
      const response = await axios.get("http://127.0.0.1:5000/patients");
      setPatients(response.data);
    } catch (error) {
      console.error("Error fetching patients:", error);
      setNotification("Failed to load patients.");
    } finally {
      setIsLoading(false);
    }
  };

  const addPatient = async (newPatient) => {
    try {
      const response = await axios.post("http://127.0.0.1:5000/patients", newPatient);
      setPatients([...patients, response.data]);
      setNotification("Patient added successfully!");
    } catch (error) {
      console.error("Error adding patient:", error);
      setNotification("Error adding patient.");
    }
  };

  return (
    <div>
      <h1>Healthcare Application</h1>
      {notification && <p className="notification">{notification}</p>}
      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <>
          <PatientList patients={patients} />
          <AddPatientForm addPatient={addPatient} />
        </>
      )}
    </div>
  );
}

export default App;
